/**
 * 重新发送注册码邮件脚本。
 * 
 * @since 2018-01-28
 * @version 1.0
 * @author Luowen
 */


/**
 * 页面基本逻辑管理。
 * 
 * @return {void}
 */
((function pageManage() {
	var oVueOption = {
		el: "#pbody",
		data: {
			username: "",
			email: "",
		},
		mounted: function() {
			document.querySelector("[name=username]").focus();
		},
		methods: {
			/**
			 * 注册。
			 */
			regist: function() {
				var _this = this;

				axios.get(TOP_DOMAIN + "api/base/user/resendRegistCode", {
					params: {
						token: TOKEN,
						username: _this.username,
						email: _this.email,
					}
				}).then(function(oAjax) {
					var oAjaxData = oAjax.data;

					if (oAjaxData.c == 1) {
						_this.$Message.success("发送成功，新注册码已经发送至您的邮箱");

						_this.username = "";
						_this.email = "";
					} else {
						_this.$Message.error("发送失败，" + oAjaxData.d);
					}
				});
			}
		}
	};
	
	
	var vVue = new Vue(oVueOption);
	oVueOption = null;
}))();
